if (/Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent)) { window.isMobile = !0 }

function t_throttle(fn, threshhold, scope) {
    var last;
    var deferTimer;
    threshhold || (threshhold = 250);
    return function() {
        var context = scope || this;
        var now = +new Date();
        var args = arguments;
        if (last && now < last + threshhold) {
            clearTimeout(deferTimer);
            deferTimer = setTimeout(function() {
                last = now;
                fn.apply(context, args)
            }, threshhold)
        } else {
            last = now;
            fn.apply(context, args)
        }
    }
}

function t456_setListMagin(recid, imglogo) { var rec = document.getElementById('rec' + recid); if (!rec || window.innerWidth <= 980) return; var menu = rec.querySelector('.t456'); var leftSide = menu ? menu.querySelector('.t456__leftwrapper') : null; var list = menu ? menu.querySelector('.t456__list') : null; var leftSideWidth = leftSide ? leftSide.offsetWidth : 0; if (list) list.style.marginRight = (imglogo ? leftSideWidth : leftSideWidth + 30) + 'px' }

function t389_scrollToTop() {
    var duration = 700;
    var difference = window.pageYOffset;
    var step = 10 * difference / duration;
    var timer = setInterval(function() {
        difference -= step;
        window.scrollTo(0, difference);
        document.body.setAttribute('data-scrollable', 'true');
        if (window.pageYOffset === 0) {
            document.body.removeAttribute('data-scrollable');
            clearInterval(timer)
        }
    }, 10)
}